            i = i + 1;

            proof { Self::abstractify_distributes_over_skip(self.un_acked@.skip(i - 1 as int), 1); }
        }

        self.num_packets_acked = seqno_acked;
        self.un_acked = self.un_acked.split_off(i); // snip!
